import React, { useState, useEffect } from "react";

const categoryMap = {
  1: "מתמטיקה",
  2: "אנגלית",
  3: "מדעים",
  4: "היסטוריה",
};

const LearningHistory = () => {
  const [items, setItems] = useState([]);
  const [filterCategory, setFilterCategory] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:5084/api/Prompts");
        const data = await response.json();
        setItems(data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const filteredItems = filterCategory
    ? items.filter((item) => item.categoryId === filterCategory)
    : items;

  return (
    <div style={{ fontFamily: "Arial", padding: "20px" }}>
      <h2 style={{ marginBottom: "10px" }}>רשימה דינמית משרת</h2>

      {/* כפתורי סינון */}
      <div style={{ marginBottom: "15px" }}>
        {Object.entries(categoryMap).map(([id, name]) => (
          <button
            key={id}
            onClick={() => setFilterCategory(Number(id))}
            style={{
              margin: "5px",
              padding: "8px 12px",
              borderRadius: "8px",
              border: "1px solid #ccc",
              cursor: "pointer",
            }}
          >
            {name}
          </button>
        ))}
        <button
          onClick={() => setFilterCategory(null)}
          style={{
            margin: "5px",
            padding: "8px 12px",
            borderRadius: "8px",
            border: "1px solid #ccc",
            cursor: "pointer",
            backgroundColor: "#f5f5f5",
          }}
        >
          הצג הכל
        </button>
      </div>

      {/* הצגת פריטים */}
      <div style={{ display: "grid", gap: "10px" }}>
        {filteredItems.map((item) => (
          <div
            key={item.id}
            style={{
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "12px",
              boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
              backgroundColor: "#fff",
            }}
          >
            <p style={{ margin: "0 0 5px 0", fontWeight: "bold" }}>
              {item.PromptText,""}
            </p>
            <p style={{ margin: 0, color: "#555" }}>
              קטגוריה: {categoryMap[item.categoryId] || "לא ידוע"}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LearningHistory;

















