import React, { useState, useEffect } from "react";

function Nuv() {
  const [users, setUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState(null); // המידע על המשתמש המחובר
  const [username, setUsername] = useState("");
  const [userphone, setUserPhone] = useState("");
  const [role, setRole] = useState("user"); // user או admin
  const [errorMessage, setErrorMessage] = useState("");

  // טוענים את רשימת המשתמשים אם המשתמש הוא מנהל
  useEffect(() => {
    if (currentUser?.role !== "admin") return;

    const fetchUsers = async () => {
      try {
        const response = await fetch("http://localhost:5084/api/User");
        const data = await response.json();
        setUsers(data);
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };

    fetchUsers();
  }, [currentUser]);

  // פונקציה לרישום רגיל (משתמש חדש)
  const registerUser = async () => {
    if (!username || !userphone) {
      setErrorMessage("Please provide username and phone number");
      return;
    }

    const newUser = { name: username, phone: userphone, role: "user" };
    try {
      const response = await fetch("http://localhost:5084/api/User/CreateUser", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser),
      });

      if (!response.ok) throw new Error("Failed to create user");
      const createdUser = await response.json();
      setErrorMessage("");
      console.log("User created:", createdUser);
    } catch (error) {
      console.error(error);
      setErrorMessage("Error creating user");
    }
  };

  // פונקציה לרישום על ידי מנהל
  const registerUserByAdmin = async () => {
    if (currentUser?.role !== "admin") {
      alert("You do not have permission to create users as admin");
      return;
    }

    const newUser = { name: username, phone: userphone, role: role };
    try {
      const response = await fetch("http://localhost:5084/api/User/CreateUser", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newUser),
      });

      if (!response.ok) throw new Error("Failed to create user");
      const createdUser = await response.json();
      setErrorMessage("");
      console.log("User created by admin:", createdUser);
      // עדכון רשימת המשתמשים
      setUsers((prev) => [...prev, createdUser]);
    } catch (error) {
      console.error(error);
      setErrorMessage("Error creating user by admin");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>User Management</h2>

      {!currentUser && (
        <div>
          <h3>Register as User</h3>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="text"
            placeholder="Phone number"
            value={userphone}
            onChange={(e) => setUserPhone(e.target.value)}
          />
          <button onClick={registerUser}>Register</button>
          <p style={{ color: "red" }}>{errorMessage}</p>
        </div>
      )}

      {currentUser?.role === "admin" && (
        <div style={{ marginTop: "30px" }}>
          <h3>Admin: Create User</h3>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="text"
            placeholder="Phone number"
            value={userphone}
            onChange={(e) => setUserPhone(e.target.value)}
          />
          <select value={role} onChange={(e) => setRole(e.target.value)}>
            <option value="user">User</option>
            <option value="admin">Admin</option>
          </select>
          <button onClick={registerUserByAdmin}>Create User</button>

          <h3 style={{ marginTop: "20px" }}>All Users</h3>
          <ul>
            {users.map((u) => (
              <li key={u.id}>
                {u.name} - {u.phone} - Role: {u.role}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Nuv;
