import { useState } from "react";
import Welcome from './Welcome';
import ChooseTopic from './ChooseTopic';
import LearningHistory from './LearningHistory';


function Login() {

    const [username, setUsername] = useState("");
    const [exsist, setExsist] = useState(false);
    const [userphone, setUserPhone] = useState("");
    const [needToLogin, setNeedToLogin] = useState(false);

    const logUp = async () => {
        const user = {
            Name: username,
            Phone: userphone
        };
        console.log(user);
        await fetch(`http://localhost:5084/api/User`, user)
            .then((response) => {
                if (response.ok) {
                    console.log("user created");
                }
                else {
                    console.log("error creating user");
                }
            })
    }


    const checkIfExsist = async () => {
        const response = await fetch(`http://localhost:5084/api/User/byName/${username}`)
            .then((response) => {
                if (!response.ok) {
                    setNeedToLogin(true);

                }
                else {
                    setExsist(true);
                }
            })

    }

   const checkIfCorrectPhone = async () => {
    try {
        const response = await fetch(`http://localhost:5084/api/User/byPhone/${userphone}`);

        if (response.status === 404) {
            console.log("משתמש לא קיים");
            return;
        }

        if (!response.ok) {
            console.error("שגיאה בשרת:", response.status);
            return;
        }

        const data = await response.json();
        console.log("משתמש נמצא:", data);
        setNeedToLogin(true);

    } catch (error) {
        console.error("שגיאה ברשת:", error);
    }
};



    return (
        <>
            {!needToLogin ? (<div className="Login">
                <h2>Login Page</h2>

                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="insert your username"
                />
                <button onClick={checkIfExsist}>ok</button>
                {exsist && <><input
                    type="text"
                    value={userphone}
                    onChange={(e) => setUserPhone(e.target.value)}
                    placeholder="insert your phone number"
                />
                    <button onClick={checkIfCorrectPhone}>insert</button></>}

            </div>) : (
                <>
                    <h3>log up</h3>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="insert your username"
                    />
                    <input
                        type="text"
                        value={userphone}
                        onChange={(e) => setUserPhone(e.target.value)}
                        placeholder="insert your phone number"
                    />
                    <button onClick={logUp}>log up</button>
                </>
            )}


            {/* <Welcome></Welcome>
                    <ChooseTopic></ChooseTopic>
                    <LearningHistory></LearningHistory> */}
        </>

    );
}



export default Login;
