import { useState } from "react";
import Welcome from './Welcome';
import ChooseTopic from './ChooseTopic';
import LearningHistory from './LearningHistory';


function Login() {

    const [username, setUsername] = useState("");
    const [exsist, setExsist] = useState(false);
    const [userphone, setUserPhone] = useState("");
    const [needToLogin, setNeedToLogin] = useState(false);
    const [loggedIn, setLoggedIn] = useState(false);
    const [errorMessage, setErrorMessage] = useState("");


    const logUp = async () => {
        const user = {
            Name: username,
            Phone: userphone
        };
        console.log(user);
        await fetch(`http://localhost:5084/api/User`, user)
            .then((response) => {
                if (response.ok) {
                    console.log("user created");
                }
                else {
                    console.log("error creating user");
                }
            })
    }


   const checkIfExsist = async () => {
        try {
            const response = await fetch(`http://localhost:5084/api/User/byName/${username}`);
            
            if (response.status === 404) {
                setNeedToLogin(true);
                setExsist(false);
                setErrorMessage("שם המשתמש אינו קיים");
                return;
            }

            if (!response.ok) {
                console.error("server error", response.status);
                setErrorMessage("שגיאת שרת, נסה שוב מאוחר יותר");
                return;
            }

            setExsist(true);
            setErrorMessage(""); // מנקים שגיאה אם נמצא
            const data = await response.json();
            console.log("משתמש נמצא:", data);

        } catch (error) {
            console.error("שגיאה ברשת:", error);
            setErrorMessage("שגיאת רשת, נסה שוב");
        }
    };
    const checkIfCorrectPhone = async () => {
        try {
            const response = await fetch(`http://localhost:5084/api/User/byPhone/${userphone}`);

            if (response.status === 404) {
                console.log("phone is not correct");
                return;
            }

            if (!response.ok) {
                console.error("server error", response.status);
                return;
            }

            const data = await response.json();
            console.log("found user", data);
            setLoggedIn(true);
        } catch (error) {
            console.error("שגיאה ברשת:", error);
        }
    };



    return (
        <>
            {!loggedIn ? (!needToLogin ? (<div className="Login">
                <h2>Login Page</h2>
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="insert your username"
                />
<p style={{ color: "red" }}>
  {errorMessage || "\u00A0"}
</p>                <button onClick={checkIfExsist}>ok</button>
                {exsist && <><input
                    type="text"
                    value={userphone}
                    onChange={(e) => setUserPhone(e.target.value)}
                    placeholder="insert your phone number"
                />
                    <button onClick={checkIfCorrectPhone}>insert</button></>}
            </div>) : (
                <>
                    <h3>log up</h3>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="insert your username"
                    />
                    <input
                        type="text"
                        value={userphone}
                        onChange={(e) => setUserPhone(e.target.value)}
                        placeholder="insert your phone number"
                    />
                    <button onClick={logUp}>log up</button>
                </>
            )) : (
                <>
                    <Welcome></Welcome>
                    <ChooseTopic></ChooseTopic>
                    <LearningHistory></LearningHistory>
                </>
            )}



        </>

    );
}



export default Login;
