import React, { useState } from "react";
import Prompt from "./Prompt";

const subtopics = {
  // קטגוריה 1
  1: {
    name: "Mathematics",
    subcategories: {
      // תתי קטגוריה של מתמטיקה
      101: "Algebra",
      102: "Geometry",
      103: "Calculus",
    },
  },
  // קטגוריה 2
  2: {
    name: "Science",
    subcategories: {
      // תתי קטגוריה של מדע
      201: "Physics",
      202: "Chemistry",
      203: "Biology",
    },
  },
  // קטגוריה 3
  3: {
    name: "History",
    subcategories: {
      // תתי קטגוריה של היסטוריה
      301: "Ancient",
      302: "Modern",
      303: "World Wars",
    },
  },
  // קטגוריה 4
  4: {
    name: "Literature",
    subcategories: {
      // תתי קטגוריה של ספרות
      401: "Poetry",
      402: "Novels",
      403: "Drama",
    },
  },
};
function ChooseTopic() {
    const [selectedTopic, setSelectedTopic] = useState("");
    const [selectedSubtopic, setSelectedSubtopic] = useState("");

    const handleTopicChange = (e) => {
        setSelectedTopic(e.target.value);
        setSelectedSubtopic(""); // Reset subtopic when topic changes
    };

    return (
        <div>
            <h2>Choose a topic to learn about:</h2>
            <select
                value={selectedTopic}
                onChange={handleTopicChange}
            >
                <option value="">-- Select a topic --</option>
                <option value="Mathematics">Mathematics</option>
                <option value="Science">Science</option>
                <option value="History">History</option>
                <option value="Literature">Literature</option>
            </select>

            {selectedTopic && (
                <>
                    <h3>Choose a subtopic:</h3>
                    <select
                        value={selectedSubtopic}
                        onChange={e => setSelectedSubtopic(e.target.value)}
                    >
                        <option value="">-- Select a subtopic --</option>
                        {subtopics[selectedTopic].map(sub => (
                            <option key={sub} value={sub}>{sub}</option>
                        ))}
                    </select>
                </>
            )}

            {selectedSubtopic && (
                <>
                <p>
                    You selected: {selectedTopic} - {selectedSubtopic}
                </p>
                <Prompt category={selectedTopic} subCategory={selectedSubtopic}></Prompt>
                </>
            )}
        </div>
    );
}

export default ChooseTopic;
