import { useState } from "react";


function Login() {

    const [username, setUsername] = useState("");
    const [exsist, setExsist] = useState(false);
    const [userphone, setUserPhone] = useState("");
    const [needToLogin, setNeedToLogin] = useState(false);

    const loginNewUser = () => {
        console.log("Creating new user:", username);
    }


    const checkIfExsist = async () => {
        const response = await fetch(`http://localhost:5084/api/User/byName/${username}`)
            .then((response) => {
                if (!response.ok) {
                    needToLogin(true);
                    loginNewUser();
                }
                else {
                    setExsist(true);
                }
            })

    }


    return (
        <div className="Login">
            <h2>Login Page</h2>
            {!needToLogin && <div className="insert">
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="insert your username"
                />
                <button onClick={checkIfExsist}>ok</button>
                {exsist && <input
                    type="text"
                    value={userphone}
                    onChange={(e) => setUserPhone(e.target.value)}
                    placeholder="insert your phone number"
                />}
                                <button onClick={checkIfExsist}>ok</button>

            </div>}
        </div>
    );
}
export default Login;
