import { useState } from "react";


function Login() {

    const [username, setUsername] = useState("");
    const [exsist,setExsist]=useState(false);

    const loginNewUser = () => {
        console.log("Creating new user:", username);
    }

    const checkIfExsist = () => {
        const response=fetch(`http://localhost:5084/api/User/byName/${username}`)
            .then((response) => {
                if (!response.ok) {
                    loginNewUser();
                }
                else{
                    setExsist(true);
                }})
                
            }
        

    return (
        <div className="Login">
            <h2>Login Page</h2>
            <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="insert your username"
            />
            <button onClick={checkIfExsist}>ok</button>
            {exsist&&<input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="insert your username"
            />}
        </div>
    );
}
export default Login;
